CREATE TABLE IF NOT EXISTS `assistenza` (
  `idAssistenza` int(4) NOT NULL,
  `Titolo` varchar(45) NOT NULL,
  `Categoria` varchar(45) NOT NULL,
  `isHighlights` tinyint(1) NOT NULL,
  `Descrizione` text NOT NULL,
  `Tipo` varchar(10) NOT NULL,
  PRIMARY KEY (`idAssistenza`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

--
-- Dump dei dati per la tabella `assistenza`
--

INSERT INTO `assistenza` (`idAssistenza`, `Titolo`, `Categoria`, `isHighlights`, `Descrizione`, `Tipo`) VALUES
(1, 'Attiva linea di casa', '1', 0, '<b>Richiedere l’attivazione di una linea telefonica di casa è molto semplice, puoi:</b><br/><br/>\r\n\r\nrichiederla online<br/>\r\nchiamare il Servizio Clienti linea fissa 187<br/>\r\nrecarti presso un Negozio STIM.<br/>\r\nVerifica la modalità di attivazione consultando on line le varie offerte disponibili.<br/><br/>\r\n\r\n<b>I dati necessari sono:</b><br/><br/>\r\n\r\nnome e cognome<br/>\r\ncodice fiscale<br/>\r\nindirizzo dell''abitazione per cui richiedi l''installazione della linea<br/>\r\nun recapito telefonico di cellulare<br/>\r\nindirizzo email (facoltativo).<br/>\r\nIl nostro personale tecnico ti contatterà quanto prima per concordare l’appuntamento per l’installazione dell’impianto telefonico.<br/>\r\n', 'Fisso'),
(2, 'Trasloco', '1', 0, '<b>Trasloco</b><br/>\r\nIl trasloco è un''operazione che ti consente di spostare la tua linea telefonica presso una nuova abitazione, nella stessa città o in una città diversa. Puoi gestirla direttamente online basta registrarsi all’Area Clienti.                                                                                                                                                           Cos''è il trasloco di una linea telefonica?<br/><br/>\r\nIl trasloco ti permette di trasferire la tua linea telefonica presso una nuova abitazione, nella stessa città o in una città diversa.<br/>\r\n', 'Fisso'),
(3, 'Restituzione prodotti', '1', 1, '<b>Restituzione prodotti</b><br/><br/>\r\n\r\n<b>Come restituire un telefono o un modem STIM</b><br/><br/>\r\nDescrizione<br/><br/>\r\n\r\nPer cessare il noleggio di un prodotto, con la conseguente interruzione del relativo abbonamento, basta chiamare il Servizio Clienti linea fissa 187 e poi consegnare o spedire il prodotto insieme al modulo di accompagnamento che puoi scaricare da questa pagina.\r\nSe hai chiesto il recesso dal Contratto di Noleggio e Manutenzione devi restituire il prodotto in noleggio entro e non oltre 30 giorni dalla data della richiesta di cessazione/recesso del contratto, e consegnarlo presso i seguenti Negozi STIM:<br/><br/>\r\n\r\nROMA:           Viale Liegi, 31<br/>\r\nMILANO:        Piazza Cordusio snc - ang. Via Broletto<br/>\r\nNAPOLI:         Centro Direzionale - Isola A4<br/>\r\nPALERMO:     Via della libertà, 37/d<br/>\r\nTREVISO:       Via XX Settembre, 2<br/>\r\noppure spedirlo a mezzo pacco postale a: <br/><br/>\r\n\r\n        Telecom Italia S.p.A. c/o Geodis Logistics<br/>\r\n        Magazzino Reverse A22<br/>\r\n        Piazzale Giorgio Ambrosoli snc<br/>\r\n        27015 Landriano PV<br/>\r\n', 'Fisso'),
(4, 'Tutto sulla tua sim', '1', 1, '<b>Tutto ciò che devi sapere per gestire al meglio la tua SIM:</b><br/><br/>\r\n\r\n<b>I codici PIN e PUK</b><br/>\r\nDescrizione<br/><br/>\r\n\r\nTutte le informazioni per gestire i codici di sicurezza PIN e PUK e per sapere cosa fare in caso di blocco della tua SIM.<br/><br/>\r\n<b>FAQ</b><br/><br/>\r\n<b>Cosa sono i Codici PIN e PUK?</b><br/><br/>\r\nIl PIN (Personal Identity Number) è un codice numerico di 4 cifre che ti consente, se regolarmente impostato, di proteggere il telefonino da utilizzi illeciti. Viene infatti richiesto dal telefonino al momento dell’accensione; il codice PIN è personalizzabile. Il PUK (Personal Unblocking Key) è un codice di sblocco fisso e non modificabile che serve per sbloccare la SIM a seguito dell''errata digitazione del codice PIN per 3 volte di seguito.<br/><br/>\r\n\r\n<b>Dove posso trovare i codici PIN e PUK?</b><br/>\r\n<b>Ho bloccato la mia SIM. Come posso sbloccarla?</b><br/>\r\n<b>Ho digitato in maniera errata il codice PUK. Come posso sbloccare la mia SIM?</b><br/><br/>\r\n\r\n<b>Cosa fare in caso di furto o smarrimento</b><br/>\r\nDescrizione<br/><br/>\r\n\r\nDi seguito puoi trovare tutte le indicazioni su cosa fare nel caso in cui ti sia stato rubato il telefonino o lo abbia smarrito. Verifica come bloccare il tuo telefonino e la tua SIM e scopri come fare per non perdere il tuo numero telefonico.<br/><br/>\r\n\r\n\r\n<b>FAQ</b><br/><br/>\r\n\r\nHo subito il furto del mio telefonino. Cosa devo fare?<br/>\r\nIn caso di furto o smarrimento del tuo telefonino dovrai tempestivamente bloccare la linea relativa alla SIM presente al suo interno.<br/>\r\nSe sei un cliente ricaricabile e sei già registrato alla sezione MySTIM Mobile puoi bloccare la linea direttamente online. Se non sei registrato alla sezione MySTIM Mobile o se sei un cliente in abbonamento dovrai contattare il Servizio Clienti 119. Previa identificazione e verifica della titolarità del numero (attraverso la comunicazione di nome, cognome, codice fiscale,...) verrà immediatamente bloccata la linea al traffico telefonico.<br/><br/>\r\n\r\nSe desideri bloccare anche il telefonino rubato o smarrito cosicché non sia più possibile utilizzarlo sulla rete degli operatori mobili nazionali potrai inviarci un''autodichiarazione di furto o smarrimento attraverso l''apposito modulo di richiesta "Blocco IMEI" indicando il codice IMEI del telefonino da bloccare e allegando copia del documento di riconoscimento. Puoi compilare ed inviarci il modulo direttamente online oppure scaricarlo in formato PDF e spedirlo all''indirizzo indicato nel modulo stesso.<br/><br/>\r\n\r\nVai nella sezione MODULI.<br/><br/>\r\n\r\nTi ricordiamo che l''IMEI è un codice composto di 15 cifre ed è riportato su di una etichetta all''interno del telefonino o sulla confezione originale dello stesso; si può inoltre individuare digitando sulla tastiera del telefonino il seguente codice: *#06#. Nel caso non abbia conservato la confezione del telefonino o non abbia provveduto in passato a memorizzare il codice IMEI, è possibile risalire allo stesso comunicando a STIM, attraverso il Modulo di richiesta Blocco IMEI, quattro numeri di telefono che chiami abitualmente.\r\nNel caso in cui sporgessi denuncia presso le competenti Autorità di Pubblica Sicurezza puoi allegarne copia alla suddetta richiesta. Il servizio di blocco IMEI è gratuito.<br/><br/>\r\n\r\n\r\n<b>Sostituzione della tua SIM</b><br/>\r\n. Descrizione<br/>\r\nL’operazione di sostituzione SIM Card, ti consente di cambiare la tua SIM e conservare il tuo numero telefonico. Puoi recarti in un qualsiasi Negozio TIM e farne richiesta.<br/>\r\n<b>TROVA NEGOZIO</b><br/>\r\n<b>FAQ</b><br/><br/>\r\nCosa si intende per sostituzione SIM Card?<br/>\r\nL’operazione di sostituzione SIM ti consente di cambiare la tua SIM e conservare il tuo numero telefonico (sia per clienti ricaricabili che in abbonamento).<br/><br/>\r\n\r\nTi ricordiamo che la sostituzione della SIM Card genera la perdita dei dati memorizzati contenuti al suo interno. In caso tu non abbia subìto il furto o lo smarrimento della carta oppure il blocco per codice PIN errato, informati su come trasferire i dati dalla memoria della scheda a quella del telefonino prima di procedere con la sostituzione della SIM Card.<br/><br/>\r\n\r\n\r\n<b>Reintegro della numerazione prepagata</b><br/><br/>\r\nIn caso di scadenza della tua TIM Card, hai la possibilità di richiedere gratuitamente, entro il termine massimo di 11 mesi dalla sua scadenza al 13° mese, il reintegro ossia la riattivazione del numero.<br/><br/>\r\n\r\n\r\n<b>Descrizione</b><br/><br/>\r\n\r\nLa STIM Card ha una durata di 12 mesi decorrenti (a) dalla sua attivazione, oppure (b) dall''ultima operazione di ricarica, oppure (c) da altro evento commerciale, di vendita o post-vendita, esplicitamente richiesto dal cliente - gratuito od a pagamento - o che comunque determina oneri a suo carico (secondo quanto previsto dalle relative condizioni di offerta), e consente di ricevere telefonate e messaggi per un ulteriore periodo di un mese.<br/><br/>\r\n\r\nLa tua STIM Card e la numerazione ad essa associata, saranno quindi disattivate trascorsi 13 mesi (12+1) qualora tu non proceda ad effettuare operazioni di ricarica, oppure in assenza di altro evento commerciale, di vendita o post-vendita, da te esplicitamente richiesto - gratuito od a pagamento - o che comunque determina oneri a tuo carico (secondo quanto previsto dalle relative condizioni di offerta; es.: l''attivazione di un’offerta o di un servizio, un cambio profilo tariffario, una SOS ricarica, etc..).<br/><br/>\r\n\r\nPuoi conoscere la data di scadenza della tua STIM Card direttamente online, dopo esserti autenticato con la tua username (numero della linea STIM) e password alla sezione MySTIM Mobile.<br/><br/>\r\n\r\nA partire dal 2 settembre 2014 per l’operazione di reintegro dovrai recarti direttamente in un Negozio STIM munito di un documento di riconoscimento e del codice fiscale. Se in tuo possesso ti consigliamo di portare con te anche la STIM Card ormai scaduta, per consentire tutte le opportune verifiche da parte del Negozio STIM.\r\nLa riattivazione del tuo numero avverrà su una nuova SIM Card che potrai scegliere anche nel formato nano SIM o micro SIM. Il costo della nuova SIM Card è di 10 € (IVA inclusa).<br/><br/>\r\n\r\nA seguito del reintegro avrai:<br/><br/>\r\n\r\nil riconoscimento gratuito del credito residuo (sempre che non sia stato già restituito in altre forme).\r\nla possibilità di effettuare un cambio del profilo tariffario gratuito.\r\nUna volta reintegrato il numero, la tua nuova STIM Card sarà subito attiva per altri 12 mesi.<br/><br/>\r\n\r\nTale procedura è valida anche per la riattivazione di numerazioni cessate su richiesta esplicita del cliente.<br/><br/>\r\n\r\nSe non fai richiesta di reintegro nei termini previsti, per scelta o per dimenticanza, non sarà più possibile recuperare il numero telefonico ma avrai la possibilità, entro i 10 anni successivi alla disattivazione, di richiedere il rimborso del solo traffico acquistato e non utilizzato presente sulla linea nel momento in cui è scaduta (bonus e promozioni non sono rimborsabili).<br/>\r\nPer maggiori informazioni e modalità di restituzione credito Clicca qui e scegli il modulo di richiesta di restituzione o trasferimento del traffico acquistato e non utilizzato per clienti ricaricabili.<br/><br/>\r\n\r\n', 'Mobile'),
(5, 'Passaggio da abbonamento a ricaricabile', '1', 0, '<b>Passaggio da abbonamento a ricaricabile</b><br/><br/>\r\n\r\n\r\nSe sei titolare di una linea in abbonamento e desideri passare al servizio ricaricabile puoi farne richiesta in un Negozio STIM o inoltrando la necessaria documentazione al Servizio Clienti 119.<br/><br/>\r\n\r\n<b>TROVA NEGOZIO</b><br/>\r\nDescrizione<br/><br/>\r\n\r\nPer passare la tua utenza STIM da abbonamento a servizio ricaricabile puoi compilare il modulo di “Richiesta di migrazione da abbonamento a ricaricabile”. Il modulo è disponibile in formato pdf nella sezione Moduli e può essere scaricato direttamente dalla sottosezione “clienti con abbonamento”.<br/>\r\nPotrai inviare il modulo compilato con la richiesta di cessazione del contratto di abbonamento e la specificata volontà di passare verso servizio ricaricabile (indicando il profilo e l''eventuale opzione) e la modalità di rimborso ACI (assegno/accredito su linea ricaricabile), al numero di fax dedicato 800600119.<br/><br/>\r\n\r\nPuoi effettuare il passaggio scrivendoci o in alternativa presso un qualsiasi Negozio STIM.<br/>\r\n', 'Generali'),
(6, 'Passsaggio da ricaricabile ad Abbonamento', '1', 0, '<b>Per passare la tua linea STIM dal servizio Ricaricabile ad un Abbonamento devi recarti presso un Negozio STIM.</b><br/><br/>\r\n\r\n<b>TROVA NEGOZIO</b><br/>\r\nDescrizione<br/>\r\n\r\nSe desideri migrare  la tua linea STIM dal servizio Ricaricabile ad un Abbonamento puoi farne richiesta presso un qualsiasi Negozio STIM. <br/>\r\nRicorda di portare con te il tuo documento di riconoscimento valido ed il codice fiscale. Nel caso tu voglia intestare il contratto d’Abbonamento ad una Persona Giuridica dovrai presentare il relativo certificato di iscrizione presso il Registro delle Imprese oppure, in alternativa, Certificato di vigenza rilasciato dalla Sezione Società Commerciali, Civile e Penale, entrambi rilasciati da non oltre 6 mesi, il documento d''identità, in corso di validità ed il codice fiscale del titolare/amministratore/legale rappresentante che ha sottoscritto la richiesta di migrazione).<br/><br/>\r\n\r\n\r\nIl passaggio ad Abbonamento può essere effettuato soltanto entro il periodo di validità della linea ricaricabile, quindi prima della scadenza. Effettuando la migrazione potrai:<br/><br/>\r\n\r\nrecuperare l''eventuale traffico acquistato non utilizzato sul primo rendiconto telefonico utile. Il traffico residuo presente sulla linea Ricaricabile sarà automaticamente riconosciuto sulla prima fattura della linea mobile utile, alla voce “REST. TRAFF. RES. STIM CARD”;<br/><br/>\r\n \r\noptare, in caso di passaggio da linea ricaricabile con profilo tariffario non più commercializzato, per un nuovo profilo di abbonamento commercializzato.<br/><br/>', 'Generale'),
(7, 'Come leggere la fattura della linea fissa', '2', 0, 'La fattura della linea fissa è semplice e facile da leggere perché disegnata prendendo spunto dai suggerimenti dei clienti. <br/>La veste grafica, moderna e raffinata, utilizza un linguaggio sempre più chiaro per l’esposizione delle voci di spesa, facilitando la lettura di tutte le informazioni di cui hai bisogno e garantendo maggiore trasparenza nei contenuti e negli importi.<br/><br/>\r\n<b>FAQ</b><br/><br/>\r\n\r\n<b>Come leggo la fattura della linea fissa?</b><br/>\r\nNella prima pagina trovi:<br/><br/>\r\n\r\ni dati principali dell’intestatario della linea di casa e della fattura<br/>\r\nil riepilogo degli abbonamenti attivi <br/>\r\nun box informativo con le proposte commerciali personalizzate relative a  offerte, prodotti, servizi <br/>\r\nl’informazione sulla modalità di pagamento (bollettino, addebito su conto corrente o su carta di credito)<br/>\r\nlo stato dei pagamenti<br/>\r\nuna guida veloce che riporta, attraverso semplici icone, i riferimenti per entrare in contatto con STIM: App MySTIM Fisso per Smartphone e Tablet, sito www.tim.it (registrandoti alla sezione MySTIM Fisso), Social Network (Facebook, Youtube, Twitter, Google+), Servizio clienti 187, Negozi STIM.<br/>\r\nNella pagina del “Dettaglio dei costi”  le voci sono riordinate secondo una logica più vicina alle tue esigenze:  <br/><br/>\r\n\r\nprima trovi gli eventuali “Importi Una Tantum” cioè quelli straordinari da pagare una sola volta, <br/>\r\npoi gli “Abbonamenti” (canoni delle offerte/prodotti/servizi attivi), \r\nsegue la descrizione “Telefonate e contenuti” (consumi di traffico, sms, contenuti digitali come video on demand, ebook, ecc) \r\ned infine la voce “Altri importi” (spese di produzione e spedizione fatture, le rate dei prodotti, le indennità di ritardato pagamento, interessi legali e moratori, consegna elenchi telefonici).<br/><br/>\r\n', 'Generale'),
(8, 'Come pagare la fattua della linea fissa', '2', 1, '<b>modalità di pagamento</b><br/><br/>\r\n(Non è necessario utilizzare il bollettino premarcato allegato alla fattura, ad eccezione di COOP e Sportello Bancario)<br/>\r\n<b>Ufficio Postale</b><br/>\r\nindicando linea telefonica, importo da pagare (saldo : totale o parziale), anno e mese della fattura.\r\nSPORTELLI BANCOMAT<br/>\r\nPunti vendita LIS PAGA di Lottomatica<br/>\r\nSISAL<br/>\r\nCarta Aura Findomestic<br/>\r\nInternet Banking<br/>\r\nCityPoste Payment<br/>\r\nCOOP<br/>\r\nSportello Bancario<br/><br/> \r\n<b>FAQ</b><br/><br/>\r\n\r\nPosso richiedere la domiciliazione su carta di credito?<br/>\r\nQuesto servizio ti permette di domiciliare in maniera permanente la fattura sulla tua carta di credito American Express, BankAmericard, Key Client, CartaSi, Diners Club e Moneta. Oltre ad essere comodo e sicuro, il servizio è gratuito e ti permette anche di usufruire della dilazione del pagamento in base ai giorni di valuta previsti dall''estratto conto della tua carta di credito. Inoltre, richiedendo Pagocard ti viene rimborsato l''anticipo conversazioni interurbane versato.<br/><br/>\r\n\r\nPer aderire a Pagocard è necessario scaricare il modulo, stamparlo e seguire queste semplici istruzioni:<br/>\r\n\r\nCompila tutti i campi del modulo di adesione. Ricorda di inserire il tuo codice CIN che puoi reperire, se non sei domiciliato, all''interno del "bollettino di pagamento premarcato" o se sei già domiciliato, all''interno della fattura nella pagina in cui sono riportati i riferimenti IVA.<br/>\r\nFirma il modulo di adesione negli appositi campi.<br/>\r\nAllega al modulo di adesione una copia del bollettino di pagamento premarcato (se non domiciliato) o la copia della pagina della fattura dove sono riportati i codici Telecom Italia (se sei già domiciliato).<br/>\r\nInvia il tutto per posta in busta chiusa o via fax (dove indicato) alla società emittente la tua carta di credito.\r\nConsulta l’elenco delle Società Emittenti convenzionate.<br/>\r\nVerifica di aver compilato il modulo in tutte le sue parti poiché in caso contrario non sarà ritenuto valido.\r\nLa conferma dell''avvenuta domiciliazione avverrà direttamente sulla tua fattura.<br/>', 'Generale'),
(9, 'Richiedere un rimborso', '2', 0, '<b>Se hai effettuato un pagamento non dovuto o se hai diritto alla restituzione dell’anticipo conversazioni interurbane puoi richiederne il rimborso.</b><br/><br/>\r\n\r\n\r\nDescrizione<br/><br/>\r\n\r\n<b>Rimborso per doppio pagamento</b><br/>\r\nSe desideri chiedere il rimborso per un pagamento già effettuato, chiama il Servizio Clienti 187. Ti sarà chiesto di lasciare un tuo recapito telefonico e sarai ricontattato, entro quattro giorni lavorativi, dal nostro ufficio amministrativo per verificare i dati inerenti ai pagamenti effettuati ed eventualmente concordare le modalità per il rimborso.<br/><br/>\r\n\r\n<b>Rimborso per anticipo conversazioni nazionali</b><br/>\r\nSe hai aderito al servizio di domiciliazione bancaria della tua fattura e ti risulta ancora non accreditato il rimborso per anticipo conversazioni, chiama il Servizio Clienti 187. I nostri operatori effettueranno insieme a te tutte le opportune verifiche.<br/><br/>\r\n\r\n<b>Rimborso per fattura negativa</b><br/>\r\nPer conoscere tempi e modalità di rimborso, chiama il Servizio Clienti 187. I nostri operatori effettueranno insieme a te tutte le opportune verifiche.<br/>', 'Generale'),
(10, 'Verifica credito residuo', '2', 1, '<b>Scopri come consultare le informazioni relative al credito residuo, alle offerte e ai bonus attivi sulla tua linea.</b><br/><br/>\r\nIl credito residuo disponibile, le offerte e lo stato dei bonus attivi sulla tua linea ricaricabile, sono consultabili attraverso le seguenti modalità:<br/>\r\nDirettamente dal tuo Smartphone, accedendo all’app MySTIM Mobile, attualmente disponibile in download gratuito nelle versioni per iPhone e per smartphone con sistema operativo Android.<br/>\r\nScarica da qui la APP MySTIM Mobile<br/>\r\nIOS<br/>\r\nANDROID<br/>\r\nDirettamente online sul sito stim.it nella sezione MySTIM Mobile riservata ai nostri clienti che ti permette di verificare il credito residuo, le quantità di minuti, SMS, GB ancora disponibili, i bonus in euro  eventualmente presenti ed effettuare variazioni sulla tua linea telefonica;<br/><br/>\r\nChiamando il numero gratuito 40916, per il credito residuo. Inoltre digitando il tasto 1 puoi conoscere le offerte attive, la quantità di minuti, SMS, GB ancora disponibili e i bonus in euro eventualmente presenti;<br/>\r\nAttraverso il servizio gratuito 119sms; inviando un sms al 119 “a testo libero”(ad esempio “qual è il mio credito residuo”) il servizio ti risponderà con un sms con le informazioni richieste;<br/>\r\nAttraverso l’attivazione del servizio gratuito "STIM Ti Avvisa - info sms" che ti permette di conoscere il costo dell’ultima chiamata nazionale effettuata e il credito residuo aggiornato in tempo reale grazie alla ricezione di un sms direttamente sul tuo telefonino oppure con il servizio “STIM Ti Avvisa – offerte” puoi essere informato con un sms gratuito quando il consumo delle tue offerte attive raggiunge la percentuale da te scelta;<br/><br/>\r\n \r\nSe sulla tua linea sono presenti offerte e bonus in euro, la priorità di consumo segue generalmente il seguente criterio:<br/><br/>\r\n\r\n1. Minuti, sms, GB inclusi nell’offerta attiva;<br/><br/>\r\n\r\n2. Bonus in euro con scadenza;<br/><br/>\r\n\r\n2. Bonus in euro senza scadenza;<br/><br/>\r\n\r\n3. Credito residuo.<br/><br/>\r\n\r\nIn presenza di due o più bonus è prioritario quello con scadenza più immediata.<br/><br/>\r\n\r\nTi ricordiamo che il bonus in euro non può essere utilizzato per le chiamate internazionali, per le chiamate verso numerazioni non geografiche (in Italia e all’estero) e per il traffico roaming (ad eccezione del traffico roaming dati).<br/><br/>\r\n\r\nPer ulteriori dettagli sulle condizioni di utilizzo dei bonus in euro presenti sulla tua linea, consulta la pagina dedicata nella sezione Offerte del sito stim.it.<br/><br/>\r\n\r\nTi ricordiamo infine che per poter usufruire delle offerte e degli eventuali bonus in euro presenti sulla linea, il credito residuo della tua SIM deve essere maggiore di zero.<br/><br/>', 'Generale'),
(11, 'Configura iphone online', '3', 1, '<b>Descrizione</b><br/><br/>\r\nNel caso si dovessero riscontrare difficoltà nell’utilizzo delle funzioni che si avvalgono della connessione dati STIM è possibile eseguire i seguenti passaggi:<br/><br/>\r\n1.	Spegnere/riaccendere l’iPhone/iPad.<br/>\r\nSe eseguendo questa operazione il problema persiste:<br/>\r\n    2.   Attivare/disattivare la modalità "Uso in aereo":<br/>\r\n•	Dalla schermata Home clicca "Impostazioni".<br/>\r\n•	Attiva la modalità "Uso in aereo"  per almeno un minuto, facendo scorrere il tasto da sinistra verso destra in modo da evidenziare in blu il simbolo della "linea verticale". <br/>\r\n•	Clicca nuovamente "Uso in aereo" per disattivare tale modalità.<br/>\r\nDovrai eseguire il procedimento  inverso (tasto da destra verso sinistra).<br/>\r\nSi esegue questa operazione in quanto attivando la modalità "uso in aereo", il dispositivo viene scollegato in modo provvisorio dalla rete andando ad inibire tutte le sue funzioni Internet. Disattivando tale modalità il dispositivo ricercherà la rete TIM ed effettuerà il ripristino di tutte le impostazioni di rete facendo eseguire al proprio iPhone/iPad un mini reset.<br/>\r\nSe il problema non si risolve:<br/><br/>\r\n    3.   Eseguire la procedura di ripristino impostazione di connessione iniziale:<br/>\r\n•	Dalla schermata Home clicca "Impostazioni".<br/>\r\n•	Clicca "Generali". <br/>\r\n•	Fai scorrere il menù fino all’ultima voce e clicca "Ripristina".<br/>\r\n•	Clicca "Ripristina impostazioni  rete".<br/><br/>\r\n \r\nNota Bene: Accertarsi di selezionare la voce corretta e non selezionare "Ripristina impostazioni" altrimenti il terminale verrà resettato e tutti i dati salvati al suo interno (foto,video, rubrica, applicazioni scaricate) verranno eliminati.<br/>\r\n•	Clicca nuovamente "Ripristina".<br/>\r\nIn tal modo la rete del dispositivo tornerà allo stato originale. <br/>\r\nAvviata tale procedura il dispositivo si spegnerà e si riavvierà automaticamente.<br/>\r\nAttendi che l’iPhone/iPad si sia riavviato completamente e che abbia caricato tutte le sue impostazioni per poi testare nuovamente il servizio.<br/>\r\nÈ possibile che durante la procedura di riavvio, il dispositivo vi richieda un PIN. In tal caso inserite lo stesso PIN code con cui accedete di solito al vostro iPhone/iPad.<br/>\r\nSe il problema non si dovesse risolvere, potrebbe essere necessario aggiornare il vostro iPhone all’ultima versione rilasciata da APPLE tramite iTunes.<br/><br/>\r\n', 'Internet'),
(12, 'Connettersi ad internet con il telefonino', '3', 1, '<b>Scopri come fare per navigare in Internet col tuo telefonino, verifica se il tuo telefonino è già predisposto per la navigazione o se necessita di essere configurato</b><br/><br/>\r\n\r\nNormalmente i telefoni commercializzati da STIM sono già predisposti per il collegamento Internet in maniera automatica, altrimenti dovrai eseguire la procedura di configurazione del servizio. <br/>\r\nDi seguito i passi da effettuare per capire se il tuo telefonino è configurato:<br/><br/>\r\n\r\nSe hai un telefonino STIM, ti basterà cliccare sull’icona del Browser Internet;<br/>\r\nSe invece non hai un telefonino STIM, ti basterà cliccare sull’icona del Browser Internet e digitare l''indirizzo "wap.Stim.it".<br/>\r\nSe non riesci a collegarti in questi modi, il tuo telefonino deve essere ancora configurato.<br/>\r\nPuoi farlo manualmente consultando le guide di configurazione specifiche per il tuo dispositivo, disponibili nella sezione “Configura online", selezionando dal menù a tendina la marca e il modello. <br/>\r\nSe non ricordi il modello specifico del tuo Smartphone o Tablet, puoi verificarlo entrando in  MySTIM Mobile. Il tuo dispositivo sarà automaticamente riconosciuto e potrai visualizzarne marca e modello.<br/>\r\nPer alcuni modelli puoi configurare in maniera automatica il tuo telefonino alla navigazione e agli MMS contattando il numero gratuito 40920, opzione 4.<br/><br/>\r\n\r\nNota Bene:<br/><br/>\r\n\r\nAl momento della connessione se non hai attivato alcuna Offerta Internet, sarà applicata la tariffa base per navigare da telefonino - Flat Day.<br/>\r\nNella sezione dedicata fra le Offerte Internet proposte da STIM troverai quella più adatta alle tue esigenze.\r\nAll’interno del portale Timgate di STIM i costi dei servizi offerti variano in base al singolo contenuto scelto e sono indicati nell’area relativa ai termini e condizioni di ogni sezione: non sono pertanto compresi nei costi di navigazione.<br/>\r\nSe sei cliente di un operatore virtuale, i costi di accesso e navigazione dipendono dall''operatore mobile di appartenenza.<br/>\r\nSTIM ti permette di proteggere la tua spesa mensile per navigare su Internet con PC, Telefonino, e Tablet. Per maggiori dettagli clicca qui.<br/>\r\nPuoi decidere di bloccare il traffico dati, vedi qui come fare.<br/>', 'Internet'),
(13, 'Assistenza tecnica specializzata con SOS pc', '3', 0, '<b>Configurazione e Gestione di Firewall e Antivirus maggiormente diffusi.</b><br/><br/>\r\nAlice Total security.<br/>\r\nCollegamento alla LAN di stampanti di rete.<br/>\r\nCollegamento ad Internet delle console di giochi più diffuse: Wii, Playstation, XBox.<br/>\r\nCollegamento alla LAN di TV, LAN Media Player.<br/>\r\nConfigurazione ed utilizzo di STIMvision.<br/>\r\nIl servizio prevede un’assistenza telefonica, su appuntamento, con un tecnico che ti guiderà nella risoluzione del problema. Il costo di questo servizio è di 29,95€ (IVA inclusa) per singolo apparato e per singolo intervento richiesto. L’intervento dovrà essere inerente ad una problematica relativa ad una delle tipologie di assistenza sopra riportate. Il costo della prestazione (che potrebbe prevedere uno o più interventi, in base allo specifico problema) verrà comunque preventivamente comunicato dall’operatore del Servizio Clienti 119 al cliente; quest’ultimo dovrà accettare le condizioni economiche comunicate dall’operatore prima che l’intervento venga effettuato*. L’addebito verrà effettuato sulla prima fattura utile di linea fissa. L’importo sarà addebitato esclusivamente se l’assistenza richiesta non derivi da cause o disservizi dipendenti da TIM.<br/><br/>\r\n\r\nNel caso in cui l’assistenza telefonica non dovesse risultare sufficiente per la gestione della tua esigenza, potrai in ogni momento decidere, con l’operatore di SOS PC, se usufruire di un ulteriore servizio offerto a domicilio da TIM, il cui costo è di 94,95€ (IVA inclusa) con addebito sulla prima fattura utile di linea fissa. Nel caso venga accettato tale intervento a domicilio, non verrà addebitato il costo dell’assistenza telefonica.<br/><br/>\r\n* L’accettazione da parte del cliente, del costo dichiarato dall’operatore del Servizio Clienti 119  (calcolato in base al numero degli interventi necessari per risolvere completamente la problematica del cliente), verrà acquisito tramite registrazione verbale.<br/>', 'Generale'),
(14, 'Phishing', '3', 0, '<b>Descrizione</b><br/><br/>\r\nIl phishing è tra le più importanti truffe telematiche. Si definisce phishing una tecnica utilizzata per ottenere l’accesso ad informazioni personali con la finalità del furto di identità mediante l’utilizzo di messaggi di posta elettronica opportunamente creati per apparire autentici.<br/><br/>\r\n\r\nIl fenomeno è caratterizzato dalle continue richieste tramite email, da parte di sedicenti banche e istituti di credito, di alcuni dati sensibili e codici personali, necessari ad esempio "per l’aggiornamento programmato del software al fine di migliorare la qualità dei servizi bancari", o per altri fantasiosi motivi.<br/>\r\nSi tratta sempre di truffe; infatti nessuna banca o istituto chiederebbe mai i codici personali attraverso una mail.\r\nCiò che crea disorientamento nell''utente ingannato, che riceve questi messaggi, è l''apparente autenticità dell''azienda che richiede i dati. I truffatori telematici, infatti, sono in grado di riprodurre loghi ufficiali di aziende e istituzioni che, uniti a messaggi efficaci, possono indurre l''utente a diventare vittima inconsapevole del raggiro.<br/>\r\nIl messaggio di truffa, in realtà, rimanda ad un sito web illegale attraverso il quale potrebbe essere avviata la procedura di reperimento dei dati o informazioni riservate.<br/>\r\nIl link presente nel messaggio contenente, ad esempio, il nome di un gruppo bancario noto, nasconde sempre un indirizzo contraffatto.<br/><br/>\r\n\r\nE'' bene sapere che i più comuni browser (Explorer, Mozilla Firefox, Opera, ecc.), allertano il navigatore ogni qualvolta si accede ad un sito considerato poco sicuro. I siti illegali, infatti, vengono segnalati ad apposite strutture che si occupano poi di aggiornare i browser in modo tale da tutelare il navigatore inesperto quando naviga su siti contraffatti.<br/><br/>\r\n\r\nCome evitare il phishing<br/>\r\nPer difendersi dal phishing è importante conoscere ed osservare semplici regole:<br/>\r\n•	Gli istituti bancari e le aziende serie non richiedono mai informazioni personali attraverso un messaggio di posta elettronica.\r\nNon cliccare sul link proposto nel messaggio e non rispondere mai alle email sospette.<br/>\r\nContatta la banca che dichiara di chiedere i vostri codici personali.<br/>\r\n•	Accedi al sito dell''istituto di credito digitando l''indirizzo direttamente nella barra del programma di navigazione o attraverso i tuoi Preferiti o Segnalibri della rubrica.<br/>\r\n•	Controlla spesso i movimenti del tuo conto corrente e delle carte di credito o bancomat.<br/>\r\n•	Sbaglia volutamente la password di accesso. Nel caso di dubbio sull''autenticità del sito al quale accedi, digita una password errata: se il sistema si accorge dell''errore avrai una garanzia in più che il sito sia già in possesso dei dati.<br/>\r\n•	Cambia periodicamente la password di accesso ai siti che contengono i tuoi dati sensibili.<br/>\r\n•	Verifica la url visualizzata nel messaggio. E’ sufficiente posizionarsi con il mouse sul link in questione per vedere se corrisponde all''indirizzo effettivo del sito web che si sta visitando.<br/>', 'Generale'),
(15, 'Privacy', '3', 0, '<b>Descrizione</b><br/><br/>\r\nIl phishing è tra le più importanti truffe telematiche. Si definisce phishing una tecnica utilizzata per ottenere l’accesso ad informazioni personali con la finalità del furto di identità mediante l’utilizzo di messaggi di posta elettronica opportunamente creati per apparire autentici.<br/><br/>\r\n\r\nIl fenomeno è caratterizzato dalle continue richieste tramite email, da parte di sedicenti banche e istituti di credito, di alcuni dati sensibili e codici personali, necessari ad esempio "per l’aggiornamento programmato del software al fine di migliorare la qualità dei servizi bancari", o per altri fantasiosi motivi.<br/>\r\nSi tratta sempre di truffe; infatti nessuna banca o istituto chiederebbe mai i codici personali attraverso una mail.<br/>\r\nCiò che crea disorientamento nell''utente ingannato, che riceve questi messaggi, è l''apparente autenticità dell''azienda che richiede i dati. I truffatori telematici, infatti, sono in grado di riprodurre loghi ufficiali di aziende e istituzioni che, uniti a messaggi efficaci, possono indurre l''utente a diventare vittima inconsapevole del raggiro.<br/>\r\nIl messaggio di truffa, in realtà, rimanda ad un sito web illegale attraverso il quale potrebbe essere avviata la procedura di reperimento dei dati o informazioni riservate.<br/>\r\nIl link presente nel messaggio contenente, ad esempio, il nome di un gruppo bancario noto, nasconde sempre un indirizzo contraffatto.<br/><br/>\r\n\r\nE'' bene sapere che i più comuni browser (Explorer, Mozilla Firefox, Opera, ecc.), allertano il navigatore ogni qualvolta si accede ad un sito considerato poco sicuro. I siti illegali, infatti, vengono segnalati ad apposite strutture che si occupano poi di aggiornare i browser in modo tale da tutelare il navigatore inesperto quando naviga su siti contraffatti.<br/><br/>\r\n\r\n<b>Come evitare il phishing</b><br/>\r\nPer difendersi dal phishing è importante conoscere ed osservare semplici regole:<br/>\r\n•	Gli istituti bancari e le aziende serie non richiedono mai informazioni personali attraverso un messaggio di posta elettronica.<br/>\r\nNon cliccare sul link proposto nel messaggio e non rispondere mai alle email sospette.<br/>\r\nContatta la banca che dichiara di chiedere i vostri codici personali.<br/>\r\n•	Accedi al sito dell''istituto di credito digitando l''indirizzo direttamente nella barra del programma di navigazione o attraverso i tuoi Preferiti o Segnalibri della rubrica.<br/>\r\n•	Controlla spesso i movimenti del tuo conto corrente e delle carte di credito o bancomat.<br/>\r\n•	Sbaglia volutamente la password di accesso. Nel caso di dubbio sull''autenticità del sito al quale accedi, digita una password errata: se il sistema si accorge dell''errore avrai una garanzia in più che il sito sia già in possesso dei dati.<br/>\r\n•	Cambia periodicamente la password di accesso ai siti che contengono i tuoi dati sensibili.<br/>\r\n•	Verifica la url visualizzata nel messaggio. E’ sufficiente posizionarsi con il mouse sul link in questione per vedere se corrisponde all''indirizzo effettivo del sito web che si sta visitando.<br/>', 'Generale'),
(16, 'Proteggi la posta elettronica', '3', 0, '<b>Descrizione</b><br/><br/>\r\n\r\nMolte sono le vulnerabilità che possono derivare da un uso incauto della Posta Elettronica come ad esempio attacchi di spam (posta indesiderata), messaggi ingannevoli finalizzati al phishing (furto d’identità), virus.<br/><br/>\r\n\r\nPer difenderti dalle minacce ti basterà porre attenzione e non aprire messaggi di posta che presentano forme non convenzionali come ad esempio: <br/><br/>\r\n\r\nIndirizzo del mittente sconosciuto o non ben riconoscibile. <br/>\r\nOggetto del messaggio anomalo, scritto con caratteri strani o che indica un contenuto non ben identificabile.<br/>\r\nOggetto del messaggio del tutto assente e con mittente sconosciuto.<br/>\r\nMessaggi con allegati provenienti da sconosciuti.<br/>\r\nAllegati con formato file facilmente infettabili da virus (.exe .msi .doc …).<br/>\r\nOltre all’analisi degli elementi che compongono il messaggio di Posta Elettronica puoi difenderti installando sul tuo PC dei software, come Antivirus o Antispam, dedicati appositamente a filtrare i tuoi messaggi in modo che non contengano virus o agenti esterni Indesiderati.<br/><br/>\r\n\r\nCome per qualsiasi antivirus è importante che questi software vengano tenuti costantemente aggiornati per garantirne l’efficacia.<br/>', 'Internet'),
(17, 'Tutela dei minori', '3', 0, '<b>Descrizione</b><br/><br/>\r\nIl Portale Mobile di STIM, contiene all’interno una Sezione Sexy riservata a Clienti maggiorenni ai sensi della Classificazione Internazionale Contenuti Adult e Sexy.<br/>\r\nTi ricordiamo che sulle linee intestate a minorenni è attivo in automatico un blocco a tali contenuti tramite il servizio OKIDS. Se invece la linea è intestata ad un maggiorenne, durante l’accesso all’area Sexy, dovrai fornire un ulteriore conferma per completare l’accesso.<br/>\r\nPer gli intestatari maggiorenni quindi, non è attivo in automatico alcuna inibizione all’Area Sexy.<br/>\r\nPer accedere alle aree Adult è necessario essere abilitati tramite la richiesta di un Pin Adult.<br/>\r\nAccesso aree adult tramite richiesta codice PIN*:<br/>\r\n•	Vai sul sito STimgate;<br/>\r\n•	Inserisci il numero di codice fiscale;<br/>\r\n•	Una volta ricevuta la documentazione accertata la correttezza del codice Fiscale con l’intestatario del numero di telefono, invieremo un SMS al numero che hai inserito nel modulo, con il tuo Codice PIN personale e ti basterà poi digitarlo all’interno dell’Area Adult del Portale Mobile di STIM, per accedervi.<br/><br/>\r\n\r\nE’ possibile invece inibire l’accesso alle aree sexy e adult  tramite richiesta inserimento Blacklist*:<br/>\r\nCon l’inserimento della linea in blacklist, verrà inibita la possibilità di accedere alla sezione dedicata agli adulti * .<br/>\r\nSe la linea è già inserita in blacklist e vuoi invece sbloccarla dovrai:<br/>\r\nCompilare ed inviare online il modulo di “Richiesta per accesso ai servizi con contenuti non adatti ai minori” disponibile nella sezione “Moduli” selezionando, a seconda che tu sia un cliente ricaricabile o in abbonamento, la sottosezione di riferimento;<br/>\r\noppure, in alternativa<br/>\r\n•	Scaricare il modulo di “Richiesta per accesso ai servizi con contenuti non adatti ai minori” disponibile in formato pdf nella sezione “Moduli” selezionando, a seconda che tu sia un cliente Ricaricabile o in Abbonamento, la sottosezione di riferimento;<br/>\r\n•	Compilare il modulo in ogni sua parte prestando particolare attenzione a porre tutte le firme dove richiesto;<br/>\r\n•	Inviarlo all''indirizzo indicato nel modulo stesso, allegando una copia di un documento d’identità personale e codice fiscale.<br/>\r\n<b>Nota Bene:</b><br/>\r\nLe informazioni che ci fornirai saranno trattate da Telecom Italia in osservanza a quanto previsto dal D.Lgs. 196/03 (Codice Privacy), per finalità connesse alla fornitura del servizio. Potrai in ogni momento prendere visione dell’informativa completa accedendo al nostro sito “www.stim.com” link “Privacy”, o presso tutti i Negozi STIM.<br/><br/>\r\n \r\n* Come anche riportato nella modulistica, l''inibizione ai servizi con contenuti non adatti ai minori non è estendibile al canale web (Internet) raggiungibile con i telefonini abilitati a tale funzione.<br/>', 'Generale'),
(19, 'Carta dei servizi', '3', 0, 'La carta dei Servizi di STIM tutela l''interesse e i diritti di tutti i clienti, fissando i parametri di qualità del servizio. </br>\r\nIn questa sezione puoi scaricare il formato PDF e consultare la Carta dei Servizi disponibili anche presso i Negozi STIM</br></br>\r\n\r\n<b>Premessa </b></br>\r\nNella Carta dei Servizi TIM di Telecom Italia S.p.A. (di seguito “TIM”), redatta in base alle linee guida fissate dall''Autorità per le Garanzie nelle Comunicazioni con Delibera 179/03/CSP, sono illustrati: </br></br>\r\n•	i principi fondamentali di comportamento verso la Clientela; </br>\r\n•	le condizioni generali di offerta dei servizi; </br>\r\n•	i parametri di qualità del servizio; </br>\r\n•	le indicazioni da seguire se non sono soddisfatti i parametri di qualità. </br></br>\r\nAlla Carta dei Servizi si affiancano le Condizioni Generali di Abbonamento e le Norme d’uso del servizio prepagato che disciplinano i rapporti contrattuali in essere con la nostra clientela. </br>\r\nSpecifiche disposizioni possono essere contenute nelle condizioni contrattuali relative a singoli servizi/offerte STIM. </br>\r\n', 'Generale'),
(20, 'Verifica credito all''estero', '3', 0, 'Verifica il credito residuo mentre sei all''estero <br/>\r\nQuando sei all’estero con la tua linea ricaricabile, puoi facilmente controllare il credito residuo.\r\nScopri come.<br/><br/>\r\n\r\n<b>Descrizione</b><br/>\r\nQuando sei all’estero puoi controllare il credito residuo della tua ricaricabile STIM attraverso le seguenti modalità:<br/>\r\n•	Se il gestore locale supporta il servizio di Chiamata Diretta, puoi chiamare il 40916 anche se sei a credito zero.<br/>\r\n•	Se hai ancora credito residuo puoi utilizzare il servizio 40916 tramite SMS inviando un messaggio gratuito al 40916 con la sintassi ''CREDITO''.<br/>\r\n•	Direttamente da pc online entrando nella sezione MySTIM Mobile.<br/>\r\n•	Dal telefonino sul sito STIM.it ottimizzato per il tuo smartphone, oppure attraverso l’App MySTIM Mobile, attualmente disponibile in download gratuito nelle versioni per iPhone e per smartphone con sistema operativo Android. Se vuoi sapere quanto costa navigare in Internet quando sei all''estero, puoi consultare la sezione costi della navigazione in Internet col telefonino o col pc. In questo caso infatti, per la visualizzazione delle informazioni, sosterrai i costi e le condizioni normalmente applicate al traffico dati in roaming.<br/>\r\n•	Infine dall''estero, è possibile verificare il proprio credito digitando *123# seguito da INVIO laddove il gestore locale consenta questo servizio. Dopo alcuni istanti comparirà l''ammontare del tuo credito residuo direttamente sul display del telefonino.\r\n<br/><br/>', 'Generale'),
(21, 'Come entrare in STIM  con la tua linea mobile', '3', 1, 'Il servizio di portabilità ti consente di cambiare Operatore telefonico portando con te il tuo numero di telefonino. Scopri come Passare a STIM scegliendo la modalità che preferisci tra quelle che STIM ti mette a disposizione:<br/><br/>\r\n\r\n<b>Descrizione</b><br/><br/>\r\nIl servizio di portabilità (MNP, cioè Mobile Number Portability) ti consente di cambiare Operatore telefonico portando con te il tuo numero di telefonino. <br/><br/>\r\n\r\nPotrai passare a STIM:\r\n   1.   direttamente al telefono con un nostro operatore che ti fornirà tutti i dettagli sulle nostre Offerte. Chiedi il ricontatto di un nostro operatore, Entra in STIM!\r\n   2.   direttamente online, cliccando sul tasto “Attiva” dell’Offerta scelta. Per scoprire i vantaggi e le modalità consulta la Guida.\r\n   3.   presso un Negozio STIM portando con te un documento d''identità e la SIM del tuo attuale operatore.\r\n\r\nl momento della richiesta di portabilità riceverai una TIM Card con una numerazione provvisoria, con la quale potrai parlare, inviare SMS e utilizzare tutti gli altri servizi STIM, usufruendo da subito della tua nuova offerta.\r\nA portabilità completata (non prima di 2 giorni lavorativi dalla richiesta), in automatico, il tuo numero andrà a sovrascrivere la numerazione “transitoria” presente sulla nuova SIM di STIM.\r\nLa suddetta operazione determina la proroga della durata della STIM Card di 13 mesi, decorrenti dalla data in cui la portabilità è completata. Ti ricordiamo che nel corso del tredicesimo mese il servizio funzionerà solo per la ricezione delle chiamate e dei messaggi.\r\n\r\n\r\nTi ricordiamo infine che per sapere se un numero è STIM puoi:\r\n•	Comporre il numero 456 seguito dal numero di telefono per il quale si desidera l''informazione (esempio 4563XXabcdefg): una fonia ti dirà se il numero corrisponde ad un cliente STIM o ad un altro operatore\r\n•	Inviare un SMS (gratuito) al 456 con la seguente sintassi: INFO 3XXabcdefg\r\n', 'Mobile'),
(22, 'Riconoscimento del credito in portabilità', '3', 0, 'Con il servizio di Portabilità del numero mobile, puoi diventare cliente STIM mantenendo il numero telefonico del tuo attuale gestore di telefonia mobile. Scopri come richiedere inoltre il riconoscimento del tuo credito ancora disponibile per poterlo utilizzare comodamente dopo il passaggio in STIM.<br/><br/>\r\n\r\n\r\n<b>Descrizione</b><br/><br/>\r\nCon il servizio di Portabilità del numero mobile, puoi diventare cliente STIM mantenendo il numero telefonico del tuo attuale gestore di telefonia mobile.\r\nOltre a mantenere il tuo numero di telefonino, hai la possibilità di richiedere il riconoscimento del credito disponibile sulla tua linea, a seguito del passaggio in STIM. <br/>\r\nIl trasferimento del credito residuo dovrà essere specificato nel momento in cui effettui la richiesta di portabilità del numero sull''apposito modulo.<br/> \r\nLa prestazione del riconoscimento del credito prevede un costo che è stabilito dall''operatore di provenienza. Sarà quest’ultimo a comunicare a STIM l’importo da trasferire, che sarà costituito dal tuo credito residuo al netto del costo del servizio di trasferimento. Saranno inoltre esclusi eventuali bonus presenti sulla tua linea.<br/>\r\nQualora il tuo credito residuo sia insufficiente a coprire il costo del servizio o nel caso in cui decidessi, in sede di richiesta di portabilità, di non richiedere il riconoscimento del credito, il tuo credito continuerà ad essere trattato in base alle norme in vigore per cui ne potrai richiedere la restituzione al tuo attuale operatore mobile secondo le modalità dallo stesso previste.<br/>\r\nComunicazione per i clienti Bip Mobile che hanno fatto richiesta di portabilità su rete STIM.<br/><br/>\r\n\r\nSe hai effettuato richiesta di portabilità da Bip Mobile su rete TIM, ti informiamo che, a causa della cessazione dei servizi da parte del tuo operatore,non potrà essere riconosciuto il trasferimento del credito residuo presente sulla tua linea.<br/>\r\nPer informazioni sullo stato del tuo credito residuo e sulle procedure per la restituzione dello stesso dovrai rivolgerti direttamente a Bip Mobile.<br/> \r\nTelecom Italia precisa che non è in alcun modo responsabile neanche per eventuali ritardi nell’attivazione della portabilità derivante dalla capacità di evasione delle richieste da parte di Bip Mobile.<br/>\r\nSi precisa che, in base alle modalità di gestione indicate dall’Autorità per le Garanzie nelle Comunicazioni, attualmente le richieste di portabilità dei clienti Bip Mobile non possono più essere accettate.<br/><br/>\r\n\r\n\r\nTi ricordiamo che, attivando online una nuova linea o effettuando richiesta di portabilità online del tuo numero in STIM da un altro operatore, il primo canone anticipato e il costo di attivazione dell’offerta scelta sono gratuiti.\r\n', 'Generale'),
(23, 'Ricarica veloce', '3', 0, '<b>Descrizione</b><br/>\r\nIl servizio Ricarica Veloce dal sito stim.it ti permette di ricaricare il tuo telefonino senza bisogno di registrarti:\r\nè sufficiente inserire i tuoi dati e in 3 click la tua linea è ricaricata!<br/><br/>\r\n\r\nI tagli di ricarica partono da un minimo di 6€ ad un massimo di 150€.<br/><br/>\r\n\r\nTi verranno richieste alcune semplici informazioni quali:<br/>\r\n•	il numero STIM da ricaricare.<br/>\r\n•	un indirizzo mail al quale inviarti la notifica di operazione effettuata.<br/>\r\n•	i dati della tua carta di credito o di PayPal.<br/>\r\nSono accettate le principali carte di credito emesse in Italia aderenti ai circuiti Visa, Mastercard (tra cui CartaSì, Postepay, Clarima e Bankamericard Deutsche Credit Card), American Express, Diners e carta Aura.\r\n<br/><br/>', 'Mobile');
INSERT INTO `assistenza` (`idAssistenza`, `Titolo`, `Categoria`, `isHighlights`, `Descrizione`, `Tipo`) VALUES
(24, 'Ottenere la fattura dei tuoi acquisti', '3', 0, 'La fattura è disponibile per l’acquisto di prodotti, SIM Card, ricariche, canoni addebitati su carta di credito per offerte rateizzate e domiciliate e traffico roaming addebitato su carta di credito.<br/>\r\nLe modalità di emissione della fattura sono diverse a seconda dell''acquisto effettuato.<br/><br/>\r\n\r\n\r\n<b>Descrizione</b><br/>\r\nLa fattura è disponibile per l’acquisto di prodotti, SIM Card, ricariche, canoni addebitati su carta di credito per offerte rateizzate e domiciliate e traffico roaming addebitato su carta di credito.<br/>\r\nLe modalità di emissione della fattura sono diverse a seconda dell''acquisto effettuato.<br/>\r\nAcquisto Prodotti online\r\nPer l’acquisto dei prodotti online è sempre prevista l’emissione della fattura. Nel catalogo, gli importi indicati sono espressi in euro e si intendono comprensivi dell’IVA.<br/>\r\nLa fattura ti verrà inviata, nei giorni successivi al pagamento, in formato .pdf all’indirizzo di posta elettronica specificato nell’ordine di acquisto.<br/>\r\nInoltre, se lo desideri, puoi richiedere anche l’invio della fattura all’indirizzo di posta ordinaria che potrai indicare esplicitamente alla voce “recapito fattura”. In tal caso il documento ti sarà recapitato entro circa 10 giorni dalla data dell’ acquisto.<br/>\r\nRicordiamo che non è possibile effettuare alcuna variazione sui dati della fattura successivamente all’ordine online.\r\n<br/><br/>\r\nAcquisto STIM Card, traffico ricaricabile (ricariche), canoni relativi ad offerte con pagamento domiciliato su carta di credito, con o senza prodotto rateizzato, traffico roaming addebitato su carta di credito.<br/>\r\nPer l’acquisto di tali servizi puoi avvalerti della ricevuta emessa in automatico da alcuni canali di ricarica (in generale tutti gli scontrini, ricevute, tipologie di ricarica che riportano data/ora della ricarica, importo ed utenza sono documenti fiscalmente validi).<br/>\r\nIn alternativa puoi richiedere a TIM l’emissione di una:<br/><br/>\r\n1.	Ricevuta riepilogativa dei servizi acquistati, senza evidenza separata dell’IVA<br/>\r\n2.	Fattura con evidenza separata dell’imposta (IVA), solo per gli esercenti arti o professioni, utilizzatori finali del servizio\r\nPer la richiesta della ricevuta riepilogativa dei servizi acquistati (senza evidenza separata dell’imposta), puoi contattare telefonicamente il Servizio Clienti 119, che provvederà a dar seguito alla lavorazione.<br/>\r\nRiceverai quindi al tuo indirizzo mail o al tuo domicilio, un documento riepilogativo, valido ai fini fiscali, per le operazioni di ricarica, canoni addebitati su carta di credito di offerte domiciliate, con o senza prodotto rateizzato, traffico roaming addebitato su carta di credito per un periodo pregresso fino a 6 mesi antecedenti alla richiesta.<br/>\r\nSe sei in possesso dei requisiti previsti dalla normativa vigente, puoi richiedere la fattura con evidenza separata dell’imposta.\r\nIn tal caso, è necessario compilare ed inviare il modulo online di “Richiesta di emissione fattura” che potrai raggiungere dalla sezione MODULIselezionando la voce “Clienti Ricaricabili”.<br/><br/>\r\nLa richiesta di fattura con evidenza separata dell’IVA deve essere presentata entro 24 ore dal momento dell''acquisto della STIM Card, della ricarica o dall’avvenuto addebito del canone.<br/><br/>\r\n', 'Generale'),
(25, 'Ricaricare con Banco STIM', '3', 0, '<b>Descrizione</b><br/><br/>\r\nCon BancoSTIM puoi ricaricare la tua linea prepagata quando vuoi, semplicemente e direttamente dal tuo telefonino. Puoi scegliere se chiamare o inviare un SMS al numero gratuito 40916.\r\n<br/><br/>\r\nSe attivi BancoSTIM da una banca abilitata al servizio hai la possibilità di ricaricare anche altri telefonini (dei tuoi familiari, dei tuoi amici, ecc.).\r\n<br/><br/>\r\nPer attivare il servizio BancoSTIM sul tuo prepagato è sufficiente essere in possesso di una carta PagoBancomat o Postamat Maestro o Postepay e recarsi presso uno sportello Bancomat o Postamat di Poste Italiane di uno dei seguenti istituti abilitati:<br/>\r\n• Banche del Gruppo Intesa Sanpaolo<br/>\r\n• Banche del Gruppo Unicredit Banca<br/>\r\n• Banche del Gruppo Cariparma<br/>\r\n• Banche del Gruppo MPS<br/>\r\n• Banche del Gruppo Istituto Centrale Banche Popolari<br/>\r\n• Banche del Gruppo Istituto Centrale Credito Cooperativo<br/>\r\n• Banca delle Marche<br/>\r\n• Banca di Credito di Saluzzo<br/>\r\n• Poste Italiane<br/>\r\n• BNL<br/><br/>\r\n\r\n\r\n<b>FAQ</b><br/><br/>\r\nCome attivare BancoSTIM<br/>\r\n•	recati presso uno sportello Bancomat o Postamat di uno degli Istituti abilitati al servizio;<br/>\r\n•	inserisci la tua carta di debito (PagoBancomat/Postamat);<br/>\r\n•	digitata il codice PIN della tua carta e accedi alla sezione dei servizi disponibili;<br/>\r\n•	seleziona il servizio BancoSTIM ed indica il numero del tuo telefonino;<br/>\r\n•	scegli un tuo codice segreto numerico di 5 cifre (ad esempio: 12345) da utilizzare quando decidi di effettuare una ricarica con BancoSTIM.<br/> Se vuoi successivamente modificare il PIN autoassegnato, puoi farlo gratuitamente presso gli sportelli Bancomat e Postamat abilitati;<br/>\r\n•	ritira la ricevuta attestante l''operazione di attivazione effettuata. L''impossibilità tecnica da parte della banca del rilascio della ricevuta non comporta la mancata attivazione del servizio\r\n<br/><br/>', 'Mobile'),
(26, 'Come ricaricare nei negozi STIM', '1', 0, '<b>Descrizione</b><br/><br/>\r\nRecati in uno dei Negozi STIM e approfitta dei servizi che ti offre il tuo operatore telefonico.<br/>\r\nPuoi scegliere di acquistare la classica RicariCard di sempre o ricaricare l’importo che vuoi tu con l’esclusivo servizio della Ricarica su Misura. E in più, con il servizio Regala STIM puoi regalare una ricarica a chi vuoi tu inviando una dedica personalizzata.<br/><br/>\r\n \r\nRicariCard<br/>\r\nLa RicariCard è utilizzabile esclusivamente dai clienti ricaricabili STIM e non è rimborsabile né riutilizzabile.<br/>\r\nRicordati che puoi ricaricare la tua STIM Card ogni volta che desideri entro il periodo di validità (13 mesi dall''attivazione o dall''ultima ricarica effettuata, tenendo presente che nel corso del tredicesimo mese il servizio funzionerà solo per la ricezione delle chiamate); potrai dunque mantenere attivo il tuo numero anche ricaricando soltanto una volta ogni 13 mesi.<br/>\r\nLe RicariCard STIM sono disponibili nei tagli da 5€, 10€ e 20€ (IVA inclusa).<br/>\r\nCome si usa: gratta delicatamente la striscia argentata che trovi sul retro della RicardCard e scopri il codice segreto. <br/>\r\nPuoi ricaricare in questo modo:<br/>\r\n1.	Dal sito stim.it: Entra in MySTIM Mobile (se non hai già effettuato la login ti verrà chiesto di digitare username e password) e vai sul tab Servizi. Clicca sulla sezione Ricarica ed alla voce Ricaricard clicca sul tasto VAI. Inserisci il numero della linea TIM sulla quale vuoi effettuare la ricarica ed il codice segreto della RicariCard.<br/>\r\n2.	Chiamando il 40916: Chiama il numero gratuito 40916 dal tuo telefonino, ascolta la voce guida e segui le indicazioni.<br/>\r\n3.	Inviando un SMS al 40916: Digita RIC seguito da uno spazio e il codice segreto; Invia il messaggio al numero gratuito 40916. Con quest''ultima procedura puoi ricaricare anche dall''estero nei paesi con cui STIM ha stretto accordi di roaming e solo se non hai terminato il credito residuo.<br/>\r\nPrima di gettare la RicariCard, attendi il messaggio che ti conferma l''avvenuta ricarica.<br/>\r\nPer maggiori dettagli puoi consultare la sezione sulla Ricaricard.<br/><br/>\r\n \r\n<b>Ricarica su Misura</b> <br/>\r\nCon la Ricarica su Misura di STIM hai la massima libertà: scegli tu quanto ricaricare, a partire da 5€ anche i centesimi diventano ricarica.<br/>\r\nLa Ricarica su Misura di STIM è una modalità di ricarica che consente di scegliere liberamente quanto credito acquistare, sfruttando così anche la disponibilità di monete. Con la Ricarica su Misura infatti è possibile effettuare ricariche del taglio che preferisci, a partire da 5€, fino a 200€ con flessibilità di 1 cent. Tutti gli importi sono IVA inclusa.<br/>\r\nIl servizio è completamente gratuito e tutto l’importo ricaricato potrà essere utilizzato come traffico telefonico senza scadenza.<br/>\r\nPer maggiori dettagli puoi consultare la sezione sulla Ricarica su Misura.<br/><br/>\r\n \r\n<b>Regala STIM</b> <br/>\r\nCon RegalaSTIM puoi donare una ricarica o una delle offerte STIM ad un cliente prepagato. Il servizio permette di associare al regalo una dedica in formato SMS da dettare direttamente al negoziante.<br/>\r\nCaratteristiche:<br/>\r\n•	può essere utilizzata da chiunque, anche da clienti non STIM;<br/>\r\n•	il regalo e la dedica associata possono essere ricevuti solo dai clienti STIM ricaricabili;<br/>\r\n•	il costo del regalo corrisponde al prezzo commerciale previsto senza alcun costo aggiuntivo.<br/>\r\nIl servizio è disponibile anche dal proprio telefonino per i clienti ricaricabili STIM, chiamando il 40916, inserendo il codice della RicariCard e seguendo le istruzioni.<br/><br/>\r\n\r\nPer le ricariche e per le altre offerte trova il Negozio STIM più vicino a te.<br/><br/>\r\n', 'Mobile'),
(27, 'Come ricaricare con Lottomatica', '1', 0, '<b>Descrizione</b><br/>\r\nPuoi ricaricare il tuo telefonino STIM nelle ricevitorie, tabaccherie e bar LIS carica di Lottomatica.<br/>\r\nRicarica la tua linea ricaricabile STIM in modo comodo e veloce sotto casa grazie a Lottomatica.<br/><br/>\r\n\r\n<b>Come ricaricare</b><br/>\r\nPer ricaricare il tuo telefonino STIM recati presso un Punto LIS o in una delle ricevitorie del Lotto abilitate al servizio e segui questa facile procedura:<br/>\r\n•	digita, o fai digitare dall''operatore, sulla tastiera del terminale il numero del telefono che intendi ricaricare e l''importo della ricarica prescelta;<br/>\r\n•	l''operatore della ricevitoria ti consegnerà una stampa dei dati inseriti: verificane l''esattezza e una volta confermata, l''operatore procederà alla conferma dell''ordine di ricarica;<br/>\r\n•	potrai pagare l''importo della ricarica direttamente all''operatore, il quale ti rilascerà un apposito documento di riepilogo (scontrino non fiscale riportante i dati dell''operazione di ricarica) attestante l''avvenuto pagamento.<br/>\r\nPer il servizio di ricarica non sono previste spese aggiuntive.<br/>\r\nTagli di ricarica\r\nPresso le ricevitorie, tabaccherie e bar LIS carica di Lottomatica abilitate è possibile ricaricare con tagli che vanno da 5€ (IVA inclusa) fino ad un massimo di 200€ (IVA inclusa), anche con flessibilità di 1 cent. Scegli la tua Ricarica su Misura, l’esclusiva modalità di STIM che consente di ricaricare liberamente sfruttando anche la disponibilità di monete. Il servizio è completamente gratuito e tutto l’importo ricaricato potrà essere utilizzato come traffico telefonico senza scadenza.\r\n <br/><br/>\r\nATTENZIONE\r\nIn caso di conferma di dati errati, il cliente è comunque tenuto al pagamento della ricarica effettuata e non potrà richiedere l''annullamento dell''operazione.\r\n', 'Mobile'),
(28, 'All''estero con STIM', '1', 0, 'Usare il telefonino all’estero è facilissimo!<br/>\r\nTutti i clienti STIM con una linea ricaricabile e con un abbonamento sono automaticamente abilitati a chiamare, ricevere telefonate, inviare e ricevere SMS dall’estero.<br/>\r\nEd è facile come in Italia!<br/><br/>\r\n', 'Generale'),
(29, 'Connettersi ad internet con Galaxy S7 edge', '3', 0, '<b>Scopri come fare per navigare in Internet col tuo telefonino, verifica se il tuo telefonino è già predisposto per la navigazione o se necessita di essere configurato</b><br/><br/>\r\n\r\nNormalmente i telefoni commercializzati da STIM sono già predisposti per il collegamento Internet in maniera automatica, altrimenti dovrai eseguire la procedura di configurazione del servizio. <br/>\r\nDi seguito i passi da effettuare per capire se il tuo telefonino è configurato:<br/><br/>\r\n\r\nSe hai un telefonino STIM, ti basterà cliccare sull’icona del Browser Internet;<br/>\r\nSe invece non hai un telefonino STIM, ti basterà cliccare sull’icona del Browser Internet e digitare l''indirizzo "wap.Stim.it".<br/>\r\nSe non riesci a collegarti in questi modi, il tuo telefonino deve essere ancora configurato.<br/>\r\nPuoi farlo manualmente consultando le guide di configurazione specifiche per il tuo dispositivo, disponibili nella sezione “Configura online", selezionando dal menù a tendina la marca e il modello. <br/>\r\nSe non ricordi il modello specifico del tuo Smartphone o Tablet, puoi verificarlo entrando in  MySTIM Mobile. Il tuo dispositivo sarà automaticamente riconosciuto e potrai visualizzarne marca e modello.<br/>\r\nPer alcuni modelli puoi configurare in maniera automatica il tuo telefonino alla navigazione e agli MMS contattando il numero gratuito 40920, opzione 4.<br/><br/>\r\n\r\nNota Bene:<br/><br/>\r\n\r\nAl momento della connessione se non hai attivato alcuna Offerta Internet, sarà applicata la tariffa base per navigare da telefonino - Flat Day.<br/>\r\nNella sezione dedicata fra le Offerte Internet proposte da STIM troverai quella più adatta alle tue esigenze.\r\nAll’interno del portale Timgate di STIM i costi dei servizi offerti variano in base al singolo contenuto scelto e sono indicati nell’area relativa ai termini e condizioni di ogni sezione: non sono pertanto compresi nei costi di navigazione.<br/>\r\nSe sei cliente di un operatore virtuale, i costi di accesso e navigazione dipendono dall''operatore mobile di appartenenza.<br/>\r\nSTIM ti permette di proteggere la tua spesa mensile per navigare su Internet con PC, Telefonino, e Tablet. Per maggiori dettagli clicca qui.<br/>\r\nPuoi decidere di bloccare il traffico dati, vedi qui come fare.<br/>', 'Internet'),
